def read_dictionary(filename):
    dict = set()
    with open(filename, 'r') as f:
        for w in f:
            dict.update([w.rstrip().lower()])
    return dict


def find_omnivocalics(dict, vowels="aeiou"):
    assert len(vowels) >= 2
    omnivocalics = []
    for index, w in enumerate(dict):
        for m in re.finditer(vowels[0], w):
            omnivocalic = False
            for v in vowels[1:]:
                if w[:m.start()] + v + w[m.end():] in dict:
                    omnivocalic = True
            if omnivocalic:
                omnivocalics.append({"options": [w[:m.start()] + vowels[1] + w[m.end():], w], "questionId": index})
    return  omnivocalics

def compare_the_pairs(schmist, vowels, voweldict, v):
    for i in vowels[0:9]+vowels[12:]:
        if v is not i:
            if v == vowels[10] or v == vowels[11]:
                doublelist = []
                key = (sorted(v), i)[0][0] + "/" + (sorted(v), i)[0][1] + ", " + (sorted(v), i)[1]
                for va in v:
                    doublelist.append(find_omnivocalics(schmist, [va, i]))
                voweldict[key] = doublelist[0] + doublelist[1]
            else:
                key = sorted((v, i))[0] + ", " + sorted((v, i))[1]
                voweldict[key] = find_omnivocalics(schmist, [v, i])

def find_all_pairs(schmist):
    vowels = ["a", "aa", "e", "ee", "i", "ie", "o", "oo", "u", "uu", ["au", "ou"], ["ei", "ij"], "oe", "eu", "ui"]
    voweldict = {}
    for v in vowels:
        compare_the_pairs(schmist, vowels, voweldict, v)
    return voweldict

